Toggle Appearance.command:
const token = canvas.tokens.controlled[0];
if ( !token ) {
  ui.notifications.warn("你必须选择一个令牌");
  return;
}

const module = game.modules.get("house-divided");

const path = "modules/house-divided/assets/creatures/tokens-topdown/";
const tokenMap = {
  "vivesna-corvinarus.webp": "vivesna-corvinarus-2.webp",
  "vivesna-corvinarus-2.webp": "vivesna-corvinarus.webp",
  "housebound-2.webp": "housebound.webp",
  "housebound.webp": "housebound-2.webp",
  "stucky.webp": "stucky-2.webp",
  "stucky-2.webp": "stucky.webp",
  "shard-stalker.webp": "shard-stalker-2.webp",
  "shard-stalker-2.webp": "shard-stalker.webp",
  "herniva-2.webp": "herniva.webp",
  "herniva.webp": "herniva-2.webp",
  "red-scullion.webp": "red-scullion-2.webp",
  "red-scullion-2.webp": "red-scullion.webp",
  "chirneros.webp": "chirneros-2.webp",
  "chirneros-2.webp": "chirneros.webp",
  "spectral-dancers.webp": "spectral-dancers-2.webp",
  "spectral-dancers-2.webp": "spectral-dancers.webp",
  "flowering-shambler.webp": "flowering-shambler-2.webp",
  "flowering-shambler-2.webp": "flowering-shambler.webp",
  "swarm-of-books.webp": "swarm-of-books-2.webp",
  "swarm-of-books-2.webp": "swarm-of-books.webp"
};

const newImage = tokenMap[token.document.texture.src.replace(path, "")];
if ( !newImage ) {
  ui.notifications.warn("此令牌不支持变形");
  return;
}

let scale = token.document.texture.scaleX;
switch ( newImage ) {
  case "herniva-2.webp": scale = 1.2; break;
  case "herniva.webp": scale = 1; break;
  case "chirneros.webp": scale = 0.7; break;
  case "chirneros-2.webp": scale = 1.0; break;
  case "flowering-shambler.webp": scale = 1.2; break;
  case "flowering-shambler-2.webp": scale = 1.8; break;
  case "red-scullion-2.webp" : scale = .8; break;
  case "red-scullion.webp" : scale = 1.3; break;
}
const delay = scale !== token.document.texture.scaleX;

await token.document.update({"texture.scaleX": scale, "texture.scaleY": scale}, {animation: {duration: 1000}});
if (delay) await module.api.sleep(1000);
await token.document.update({"texture.src": path + newImage});

