Sanctum Shattering.command:
// 定义相关文档
const planar = game.scenes.get("rRY9Y8jNBkPbmcUl");
const shattered = game.scenes.get("9fjzcw7xLMsg9dYX");
const excludedActors = [
  "cEaCmjp4Fxfa3ftH", // 达里乌斯
  "Xxf80BJeTCVKhIao", // 憎恶
  "5aBfVyHdXKw8oJXb", // 精类生物水晶
  "HSfcDhqYWOyVdZqb"  // 暗影水晶
];

// 准备令牌数据
const toCreate = planar.tokens.reduce((arr, token) => {
  if ( excludedActors.includes(token.actor.id) ) return arr;
  const data = token.toObject();
  data.y += 1400;
  arr.push(data);
}, []);
const toDelete = toCreate.map(d => d._id);

// 暂停游戏
game.togglePause(true, true);

// 创建和删除令牌
await shattered.createEmbeddedDocuments("Token", toCreate);
await planar.deleteEmbeddedDocuments("Token", toDelete);

// 激活新场景
shattered.activate();

