Ochre Jelly Split.command:
if ( !token || !token.document.name.includes("Jelly")) {
  ui.notifications.warn("你必须选择一个赭冻怪令牌");
  return;
}

let update = {};

// 生命值减半
const currentHealth = token.document.actor.system.attributes.hp.value;
const newHealth = Math.floor(currentHealth / 2);
update["system.attributes.hp.value"] = newHealth;

// 选择下一级体型
const currentSize = token.document.actor.system.traits.size;
let newSize = "微型";
let scale = 1.3;
let dimensions = 0.5;
if ( currentSize == "lg" ) {
  newSize = "中型";
  dimensions = 1;
}
else if ( currentSize == "中型" ) {
  newSize = "小型";
  scale = 0.9;
  dimensions = 1;
}

// 移除AE's
update["effects"] = [];

// 执行角色更新
update["system.traits.size"] = newSize;
await token.document.actor.update(update);

// 生成新的克隆令牌
const existingToken = canvas.tokens.get(token.id);
const newToken = foundry.utils.duplicate(existingToken.document);
let created = await canvas.scene.createEmbeddedDocuments("Token", [newToken], {});

// 执行画布更新
newToken.x += canvas.dimensions.size * dimensions;
await canvas.scene.updateEmbeddedDocuments("Token", [{"_id": token.id, "texture.scaleX": scale, "texture.scaleY": scale, "width": dimensions, "height": dimensions}, {"_id": created[0]._id, "texture.scaleX": scale, "texture.scaleY": scale, "width": dimensions, "height": dimensions, "x": newToken.x}], {animation: {duration: 2000}});

