blfx | Light Emission Radius.command:
const MODULE_NAME = 'boss-loot-assets-premium';
const lights = canvas.scene.lights?.contents;
const destabilizatorImg =
  'modules/boss-loot-adventures-premium/artwork/063-paladin-tomb/art-transparent-background-destabilizator-600x600.webp';

if (!game.user.isGM) {
  ui.notifications.warn('You do not have permission to use this macro!');
  return;
}

if (lights.length === 0) {
  ui.notifications.info(`'${canvas.scene.name}' has no Lights defined!`, { console: false });
  return;
}

const flaggedLights = lights.filter(light => {
  // Check for custom flag
  const lightData = foundry.utils.getProperty(light, `flags.${MODULE_NAME}.lightData`);
  return !!lightData?.name;
});

if (flaggedLights.length === 0) {
  ui.notifications.info('This script is designed for Boss Loot custom maps and works only on those maps.', { console: false });
  return;
}

const destabilizatorTile = await fromUuid('Scene.MOhw5XBaA8s54YQu.Tile.j9DYlmIvGCzSdWwx');

let dialogLights = `<label>Update the radius of light emission!</label>`;
dialogLights += `<fieldset><legend>Lights on ${canvas.scene.name}</legend>`;

for (const light of flaggedLights) {
  if (light.isGlobal) continue;
  const lightData = foundry.utils.getProperty(light, `flags.${MODULE_NAME}.lightData`);
  if (!lightData?.name) continue;

  dialogLights += `
    <div class="form-group">
      <label>${lightData.name}</label>
      <div class="form-fields">
        <range-picker name="${light.id}" value="${light.config.dim}" min="0" max="90" step="15">
          <input type="range" min="0" max="90" step="15">
          <input type="number" min="0" max="90" step="15">
        </range-picker>
      </div>
    </div>
  `;
}
dialogLights += '</fieldset>';

let activeDialog = null;
function closeOnSceneChange() {
  if (activeDialog?.rendered) {
    console.log(`Boss Loot | Scene changed, closing the Lights Control Dialog.`);
    activeDialog.close(); // calls the instance's close() to destroy the hook
  }
}

// Render the dialog
await foundry.applications.api.DialogV2.wait({
  window: { title: 'Lights Control' },
  content: dialogLights,
  rejectClose: false,
  form: { closeOnSubmit: false },
  position: { width: 350 },
  buttons: [
    {
      action: 'close',
      label: 'Close',
      callback: (event, clickedButton, dialog) => {},
    },
  ],
  /**
   *  Attach event listeners.
   */
  render: event => {
    activeDialog = event.target;
    Hooks.on('canvasReady', closeOnSceneChange);

    activeDialog.element.querySelectorAll('range-picker[name]').forEach(rp => {
      rp.addEventListener('change', async ev => {
        const lightRange = rp.value;
        const lightId = rp.getAttribute('name');
        if (!lightId) return;

        // Dynamically update the light’s angle
        try {
          await canvas.scene.updateEmbeddedDocuments('AmbientLight', [{ _id: lightId, 'config.dim': lightRange }]);

          if (lightRange >= 75) {
            if (destabilizatorTile?.texture?.src !== destabilizatorImg) {
              await summonAnimation(destabilizatorTile);
              await destabilizatorTile?.update({ 'texture.src': destabilizatorImg });
            }
          } else {
            if (destabilizatorTile?.texture?.src) {
              await destabilizatorTile?.update({ 'texture.src': null });
            }
          }
        } catch (err) {
          console.error(err);
        }
      });
    });
  },

  close: (event, dialogInstance) => {
    // Remove the scene-change hook
    Hooks.off('canvasReady', closeOnSceneChange);
  },
});

async function summonAnimation(mapTile) {
  if (!game.modules.get('sequencer')?.active) return;
  if (!game.modules.get(MODULE_NAME)?.active) return;
  if (!mapTile) return;

  await new Sequence()
    .effect()
    .atLocation(mapTile)
    .file('blfx.spell.melee.heal2.particles1.glow.orange')
    .scale(0.25)
    .waitUntilFinished(-500)

    .play();
}

