  dialogContent += `
    <div style="display: flex; align-items: center; gap: 10px;">
      <img src="${tokenDoc.actor.img}" width="48" height="48" style="border: none;">
      <label style="flex: 1;">${tokenDoc.name}</label>
      <input type="checkbox" name="token-${tokenDoc.id}" />
    </div>
  `;
