// icons/svg/wing.svg
if (game.system.id !== 'dnd5e') {
  ui.notifications.warn('The macro works only for D&D 5e system!');
  return;
}

if (!game.user.isGM) {
  ui.notifications.warn('You do not have permission to use this macro!');
  return;
}

if (canvas.tokens.controlled.length !== 1) {
  ui.notifications.warn('Please select the dragon token!', { console: false });
  return;
}

const sourceToken = canvas.tokens.controlled[0];

if (!sourceToken.actor.name.includes('Volcathar')) {
  ui.notifications.warn('Please select Volcathar the Flameborn token!', { console: false });
  return;
}

await volcatharFly(sourceToken);

async function volcatharFly(sourceToken) {
  const MODULE_NAME = 'boss-loot-adventures-premium';

  let standPoseImg = `modules/${MODULE_NAME}/artwork/059-red-dragon/Dragon_Red_STAND_ANIMATED_TOKEN_2200x1600.webm`;
  let flyPoseImg = `modules/${MODULE_NAME}/artwork/059-red-dragon/Dragon_Red_FLY_ANIMATED_TOKEN_2200x1600.webm`;

  const dragonCR = sourceToken.actor.system.details?.cr;
  if (dragonCR >= 15) {
    standPoseImg = `modules/${MODULE_NAME}/artwork/060-red-dragon-huge/Dragon_Red_Burning_STAND_ANIMATED_TOKEN_2200x1600.webm`;
    flyPoseImg = `modules/${MODULE_NAME}/artwork/060-red-dragon-huge/Dragon_Red_Burning_FLY_ANIMATED_TOKEN_2200x1600.webm`;
  }

  const isStandPose = sourceToken.document.texture.src === standPoseImg;

  if (isStandPose) {
    // Case 1: Transition from Stand to Fly
    await playFly(sourceToken.document, flyPoseImg);
  } else {
    // Case 2: Transition from Fly to Stand
    await playFly(sourceToken.document, standPoseImg);
  }
}

async function playFly(sourceToken, tokenImg) {
  sourceToken.update({ 'texture.src': tokenImg, alpha: 1 }, { animate: false });
}

