  },
];

const lights = canvas.scene.lights?.contents || [];

const shieldLights = lights.filter(light => {
  const lightData = foundry.utils.getProperty(light, `flags.${MODULE_NAME}.lightData`);
  return !!lightData?.isShield;
});

if (shieldLights.length === 0) {
  ui.notifications.info('This script is designed for Boss Loot custom maps and works only on those maps.', { console: false });
  return;
}

let dialogContent = `<label><strong>Choose a Personality!</strong> (max 4)</label>
<div style="display: flex; flex-direction: column; gap: 10px; margin-top: 10px;">`;

for (const entry of actorSpawns) {
  const foundActor = game.actors.get(entry.id);
  if (!foundActor) continue;
  if (entry.type === 'wizard') continue;
  dialogContent += `
    <div style="display: flex; align-items: center; gap: 10px;">
      <img src="${foundActor.img}" width="48" height="48" style="border: none;">
      <label style="flex: 1;">${foundActor.name}</label>
      <input type="checkbox" name="${foundActor.id}" />
    </div>
  `;
}

dialogContent += `</div><hr>`;

// Add wizard section
const wizardEntry = actorSpawns.find(a => a.type === 'wizard');
const wizActor = game.actors.get(wizardEntry?.id);
if (wizActor) {
  dialogContent += `
    <label><strong>Complete the experiment!</strong></label>
    <div style="display: flex; flex-direction: column; gap: 10px; margin-top: 10px;">
      <div style="display: flex; align-items: center; gap: 10px;">
        <img src="${wizActor.img}" width="48" height="48" style="border: none;">
        <label style="flex: 1;">${wizActor.name}</label>
        <input type="checkbox" name="${wizActor.id}" />
      </div>
    </div>
  `;
} else {
  console.warn(`Wizard actor "${wizardEntry?.name}" not found in the world!`);
}

let activeDialog = null;
function closeOnSceneChange() {
  if (activeDialog?.rendered) {
    console.log('Scene changed. Closing the Summon Token dialog.');
    activeDialog.close(); // calls the instance's close() to destroy the hook
  }
}

// --------------------------------------------------------
// DIALOG
// --------------------------------------------------------
foundry.applications.api.DialogV2.wait({
  window: { title: 'Summon Token' },
  form: { closeOnSubmit: false },
  content: dialogContent,
  // position: { width: 300 },
  rejectClose: false,

  buttons: [
    {
      action: 'submit',
      label: 'Summon',
      default: true,
      callback: async (event, button) => {
        const data = new FormDataExtended(button.form).object;
        const selectedIds = Object.entries(data)
          .filter(([_, checked]) => checked)
          .map(([id, _]) => id);

        // Max 4 personalities or exactly wizard only
        const personalities = selectedIds.filter(id => {
          const actor = game.actors.get(id);
          return actorSpawns.some(sp => sp.id === actor?.id && sp.type === 'personality');
        });
        const wizardSelected = selectedIds.some(id => id === wizardEntry.id);

        // Restriction: 4 personalities max
        if (personalities.length > 4) {
          ui.notifications.warn('Please choose max 4 personalities or complete the experiment!', { console: false });
          return;
        }

        // Restriction: either personalities OR wizard
        if (personalities.length > 0 && wizardSelected) {
          ui.notifications.warn('Please choose max 4 personalities OR complete the experiment!', { console: false });
          return;
        }

        // Check if chosen tokens are already summoned!
        const existingActorIds = new Set(canvas.scene.tokens.map(token => token.actor.id));
        for (const id of selectedIds) {
          if (existingActorIds.has(id)) {
            const actorName = game.actors.get(id);
            ui.notifications.warn(`Token '${actorName.name}' it's already on the map! Please delete it and try again!`, { console: false });
            return;
          }
        }

        // Summon the tokens
        await summonTokens(selectedIds);

        // Clear the checkboxes
        button.form?.querySelectorAll('input[type="checkbox"]').forEach(checkbox => (checkbox.checked = false));
      },
    },
    {
      action: 'close',
      label: 'Close',
      callback: (event, clickedButton, dialog) => {},
    },
  ],

  render: (event, dialogElement) => {
    activeDialog = event.target;
    Hooks.on('canvasReady', closeOnSceneChange);
  },

  close: (event, dialogInstance) => {
    Hooks.off('canvasReady', closeOnSceneChange);
  },
});

// --------------------------------------------------------
// SUMMON
// --------------------------------------------------------
async function summonTokens(selectedIds) {
  const tokenData = [];
  let personalityIndex = 0;

  for (const id of selectedIds) {
    const sourceActor = game.actors.get(id);
    if (!sourceActor) {
      console.error(`Actor with id "${id}" not found!`);
      continue;
    }

    // If it's the wizard
    if (sourceActor.id === wizardEntry.id) {
      tokenData.push(buildTokenData(sourceActor, wizardEntry.coords));
    }
    // Else, one of the 5 personalities
    else {
      tokenData.push(buildTokenData(sourceActor, actorSpawns[personalityIndex].coords));
      personalityIndex++;
    }
  }

  // Spawn them all at once
  if (tokenData.length) {
    const tokenList = await canvas.scene.createEmbeddedDocuments('Token', tokenData);
    // canvas.tokens.releaseAll();
    if (tokenData.length === 1 && tokenData[0].actorId === wizardEntry.id) {
      await turnOffCustomLights();
      await wizardAnimation(tokenList);
    } else {
      await personalityAnimation(tokenList);
    }
  }
}

// Helper to build Token document data
function buildTokenData(actor, coords) {
  return {
    name: actor.prototypeToken?.name ?? actor.name,
    x: coords.x,
    y: coords.y,
    rotation: coords.r,
    texture: actor.prototypeToken.texture,
    width: actor.prototypeToken.width,
    height: actor.prototypeToken.height,
    sight: actor.prototypeToken.sight,
    hidden: false,
    actorId: actor.id,
    actorLink: false,
    detectionModes: actor.prototypeToken.detectionModes,
    displayBars: actor.prototypeToken.displayBars,
    disposition: actor.prototypeToken.disposition,
    displayName: CONST.TOKEN_DISPLAY_MODES.CONTROL,
  };
}

async function personalityAnimation(tokenList) {
  if (!game.modules.get('sequencer')?.active) return;
  if (!game.modules.get(MODULE_NAME)?.active) return;
  for (let targetToken of tokenList) {
    new Sequence()
      .wait(200)
      .effect()
      .atLocation(targetToken)
      .file('blfx.spell.cast.impact.electric.lightning2.color5')
      .scale(0.15)
      // .waitUntilFinished(-800)

      .play();
  }
}

async function wizardAnimation(tokenList) {
  if (!game.modules.get('sequencer')?.active) return;
  if (!game.modules.get(MODULE_NAME)?.active) return;
  const [targetToken] = tokenList;
  const backgroundImage = canvas.scene.background.src;

  if (!backgroundImage) return;

  new Sequence()

    //MAP_FLASH_1
    .effect()
    .file(backgroundImage)
    .atLocation({ x: canvas.dimensions.width / 2, y: canvas.dimensions.height / 2 })
    .size({ width: canvas.scene.width / canvas.grid.size, height: canvas.scene.height / canvas.grid.size }, { gridUnits: true })
    .duration(750)
    .fadeOut(750)
    .opacity(0.8)
    .filter('ColorMatrix', { brightness: 4, saturate: -1 })
    .filter('Blur', { blurX: 1, blurY: 1 })
    .zIndex(1)
    .aboveLighting()

    .canvasPan()
    .atLocation({ x: 1600, y: 1250 }) // Set the location
    .duration(1500) // Pan over
    .scale(1.3) // Zoom in
    .lockView(1500) // Lock the view

