async function turnOffCustomLights() {
  const customLights = lights.filter(light => {
    const lightData = foundry.utils.getProperty(light, `flags.${MODULE_NAME}.lightData`);
    return !!lightData?.name;
  });

  const updates = [];
  for (const light of customLights) {
    if (!light.hidden) {
      updates.push({
        _id: light.id,
        hidden: true,
      });
    }
  }

  // Update
  if (updates.length > 0) {
    await canvas.scene.updateEmbeddedDocuments('AmbientLight', updates);
  }
}

