}

const flaggedLights = lights.filter(light => {
  if (light.config.angle === 360 || light.config.angle === 0) return false; // Either off or emits in all directions

  // Check for custom flag
  const lightData = foundry.utils.getProperty(light, `flags.${MODULE_NAME}.lightData`);
  return !!lightData?.name;
});

if (flaggedLights.length === 0) {
  ui.notifications.info('This script is designed for Boss Loot custom maps and works only on those maps.', { console: false });
  return;
}

// Sort flaggedLights by name
flaggedLights.sort((a, b) => {
  const aName = foundry.utils.getProperty(a, `flags.${MODULE_NAME}.lightData.name`) || '';
  const bName = foundry.utils.getProperty(b, `flags.${MODULE_NAME}.lightData.name`) || '';
  return aName.localeCompare(bName);
});

