let dialogLights = `<label class="${hiddenClass}"><strong>Level 6 - Puzzle Solution</strong></label>`;
dialogLights += `
    <div class="${hiddenClass}" style="text-align: center;">
      <img src="modules/boss-loot-adventures-premium/artwork/062-wizard-tower/assets/Arcane_Lights_Puzzle_Solution_v1.webp" width="390" height="390" style="border: none;">
    </div>
`;

dialogLights += `<fieldset><legend>Lights on ${canvas.scene.name}</legend>`;

for (const light of flaggedLights) {
  if (light.isGlobal) continue;
  const lightData = foundry.utils.getProperty(light, `flags.${MODULE_NAME}.lightData`);
  if (!lightData?.name) continue;

  dialogLights += `
    <div class="form-group">
      <label>${lightData.name} Angle</label>
      <div class="form-fields">
        <range-picker name="${light.id}" value="${light.rotation}" min="0" max="360" step="1">
          <input type="range" min="0" max="360" step="1">
          <input type="number" min="0" max="360" step="1">
        </range-picker>
      </div>
    </div>
  `;
}
dialogLights += '</fieldset>';

let activeDialog = null;
function closeOnSceneChange() {
  if (activeDialog?.rendered) {
    console.log(`Boss Loot | Scene changed, closing the Lights Control Dialog.`);
    activeDialog.close(); // calls the instance's close() to destroy the hook
  }
}

// Render the dialog
await foundry.applications.api.DialogV2.wait({
  window: { title: 'Lights Control' },
  content: dialogLights,
  rejectClose: false,
  form: { closeOnSubmit: false },
  buttons: [
    {
      action: 'update',
      label: 'Power ON the Experiment!',
      default: true,
      callback: async (event, button, dialogElement) => {
        await updateLights();
      },
    },
    {
      action: 'close',
      label: 'Close',
      callback: (event, clickedButton, dialog) => {},
    },
  ],
  /**
   *  Attach event listeners.
   */
  render: event => {
    activeDialog = event.target;
    Hooks.on('canvasReady', closeOnSceneChange);

    activeDialog.element.querySelectorAll('range-picker[name]').forEach(rp => {
      rp.addEventListener('change', async ev => {
        if (rp.dataset?.skipWrap === 'true') {
          rp.dataset.skipWrap = 'false';
          return;
        }

        let angle = Number(rp.value);
        if (isNaN(angle)) return;

        // Wrap-around logic
        if (angle === 360) {
          rp.dataset.skipWrap = 'true';
          angle = 0;
          rp.value = 0;
        } else if (angle === 0) {
          rp.dataset.skipWrap = 'true';
          angle = 360;
          rp.value = 360;
        }

        const lightId = rp.getAttribute('name');
        if (!lightId) return;

        // Dynamically update the light’s rotation
        try {
          await canvas.scene.updateEmbeddedDocuments('AmbientLight', [{ _id: lightId, rotation: angle }]);
        } catch (err) {
          console.error(err);
        }
      });
    });
  },

  close: (event, dialogInstance) => {
    // Remove the scene-change hook
    Hooks.off('canvasReady', closeOnSceneChange);
  },
});

async function updateLights(result) {
  const shieldLight = lights.filter(light => {
    const lightData = foundry.utils.getProperty(light, `flags.${MODULE_NAME}.lightData`);
    return !!lightData?.isShield;
  });
  const sceneLights = [...flaggedLights, ...shieldLight];

  const updates = [];

  for (const light of sceneLights) {
    if (light.hidden) {
      updates.push({
        _id: light.id,
        hidden: false,
      });
    }
  }

  // Update
  if (updates.length > 0) {
    await canvas.scene.updateEmbeddedDocuments('AmbientLight', updates);
    ui.notifications.info(`Updated ${updates.length} light(s) !`, { console: false });
  }
}

