    // Remove the scene-change hook
    Hooks.off('canvasReady', closeOnSceneChange);
  },
});

// -------------------------------------
// Region behavior update function
// -------------------------------------
async function updateRegionBehavior(result) {
  // Iterate over all Regions
  for (const [regionId, regionData] of Object.entries(result)) {
    const region = canvas.scene.regions.get(regionId);
    if (!region) continue;

    const updates = [];

    for (const [behaviorId, behaviorInfo] of Object.entries(regionData.behaviors)) {
      const behavior = region.behaviors.get(behaviorId);
      if (!behavior) continue;

      // Compare current state vs. new state
      const currentState = behavior.disabled;
      const newState = behaviorInfo.disabled;
      if (currentState !== newState) {
        updates.push({ _id: behavior.id, disabled: newState });
      }
    }

    // If there are no updates, skip the region
    if (!updates.length) continue;

    // Update the region behaviors in Foundry
    await region.updateEmbeddedDocuments('RegionBehavior', updates);
  }
  ui.notifications.info('Region behavior(s) updated!', { console: false });
}

// -------------------------------------
// Foundry V13 compatibility check
// -------------------------------------
async function updateDeprecatedBehaviorEvents() {
  if (!foundry.utils.isNewerVersion(game.version, 13)) return;

  for (const region of regions) {
    const behaviors = region.behaviors.contents;
    if (behaviors.length === 0) continue;

    // Check for the deprecated events ("behaviorStatus")
    for (const behavior of behaviors) {
      const events = behavior.system?.events;
      if (!(events instanceof Set)) continue;
      if (events.has('behaviorStatus')) {
        console.log(`Boss Loot | Found a behavior (${behavior.name}) with deprecated events 'behaviorStatus', trying to update..`);

        // Create a new Set without 'behaviorStatus' and add the replacements
        const updatedEvents = new Set(events);
        updatedEvents.delete('behaviorStatus'); // CONST.REGION_EVENTS.BEHAVIOR_STATUS
        updatedEvents.add(CONST.REGION_EVENTS.BEHAVIOR_ACTIVATED); // behaviorActivated
        updatedEvents.add(CONST.REGION_EVENTS.BEHAVIOR_DEACTIVATED); // behaviorDeactivated

        // Update the behavior
        await behavior.update({ 'system.events': Array.from(updatedEvents) });
      }
    }
  }
}

