async function updateLights(result) {
  const updates = [];

  for (const [lightId, checked] of Object.entries(result)) {
    const light = canvas.scene.lights.get(lightId);
    if (!light) continue;

    const shouldBeHidden = !checked;
    // Only update if there's an actual change
    if (light.hidden !== shouldBeHidden) {
      updates.push({
        _id: lightId,
        hidden: shouldBeHidden,
      });
    }
  }

  // Update
  if (updates.length > 0) {
    await canvas.scene.updateEmbeddedDocuments('AmbientLight', updates);
    ui.notifications.info(`Updated ${updates.length} light(s) !`, { console: false });
  }
}

