      <label style="flex: 1;">${tokenDoc.name} ${visionDisabledLabel}</label>
      <input type="checkbox" name="${tokenDoc.id}" ${seeInvisibility ? 'checked' : ''} ${!visionEnabled ? 'disabled' : ''} />
    </div>
  `;
}

dialogContent += `</div>`;

let activeDialog = null;
function closeOnSceneChange() {
  if (activeDialog?.rendered) {
    console.log('Scene changed. Closing the BLFX dialog.');
    activeDialog.close();
  }
}

// --------------------------------------------------------
// DIALOG
// --------------------------------------------------------
foundry.applications.api.DialogV2.wait({
  window: { title: 'Boss Loot Toggle See Invisibility' },
  form: { closeOnSubmit: false },
  content: dialogContent,
  position: { width: 450 },
  rejectClose: false,

  buttons: [
    {
      action: 'submit',
      label: 'Submit',
      default: true,
      callback: async (event, button, dialogElement) => {
        const data = new FormDataExtended(button.form).object;

        await updateTokens(data);
      },
    },
    {
      action: 'close',
      label: 'Close',
      callback: (event, clickedButton, dialog) => {
        activeDialog?.close();
      },
    },
  ],

  render: (event, dialogElement) => {
    activeDialog = event.target;
    Hooks.on('canvasReady', closeOnSceneChange);
  },

  close: (event, dialogInstance) => {
    Hooks.off('canvasReady', closeOnSceneChange);
  },
});

async function playAnimation(tokenDoc) {
  if (isSequencerActive && isBlfxActive) {
    await new Sequence()
      .effect()
      .file('blfx.spell.cast.impact.electric.lightning3.color3')
      .atLocation(tokenDoc)
      .scaleToObject(1.5)
      .play();
  }
}

function toggleDetectionMode(detectionModes, toggleMode) {
  const modeIndex = detectionModes.findIndex(mode => mode.id === toggleMode.id);

  if (modeIndex !== -1) {
    // Remove mode if already present
    detectionModes.splice(modeIndex, 1);
    return { detectionModes, toggled: false };
  } else {
    // Add mode if not present
    detectionModes.push(toggleMode);
    return { detectionModes, toggled: true };
  }
}

async function updateTokens(result) {
  const updates = [];

  const seeInvisibilityData = {
    id: 'seeInvisibility',
    enabled: true,
    range: 60,
  };
  for (const [tokenId, checked] of Object.entries(result)) {
    const tokenDoc = allTokens.get(tokenId);
    if (!tokenDoc) continue;

    const seeInvisibility = tokenDoc.detectionModes.some(obj => obj.id === 'seeInvisibility');
    // Only update if there's an actual change
    if (seeInvisibility !== checked) {
      const detectionModesCopy = foundry.utils.deepClone(tokenDoc.detectionModes);
      const { detectionModes: updatedModes, toggled } = toggleDetectionMode(detectionModesCopy, seeInvisibilityData);
      updates.push({
        _id: tokenId,
        detectionModes: updatedModes,
      });

      await playAnimation(tokenDoc);
    }
  }

  // Update
  if (updates.length > 0) {
    await canvas.scene.updateEmbeddedDocuments('Token', updates);
    ui.notifications.info(`Updated ${updates.length} token(s) !`, { console: false });
  }
}

