    Hooks.on('canvasReady', closeOnSceneChange);

    dialogElement.querySelectorAll('range-picker[name]').forEach(rp => {
      rp.addEventListener('change', async ev => {
        if (rp.dataset?.skipWrap === 'true') {
          rp.dataset.skipWrap = 'false';
          return;
        }

        let angle = Number(rp.value);
        if (isNaN(angle)) return;

        // Wrap-around logic
        if (angle === 360) {
          rp.dataset.skipWrap = 'true';
          angle = 0;
          rp.value = 0;
        } else if (angle === 0) {
          rp.dataset.skipWrap = 'true';
          angle = 360;
          rp.value = 360;
