        const lightId = rp.getAttribute('name');
        if (!lightId) return;

        // Dynamically update the light’s rotation
        try {
          await canvas.scene.updateEmbeddedDocuments('AmbientLight', [{ _id: lightId, rotation: angle }]);
        } catch (err) {
          console.error(err);
        }
      });
    });
