    Hooks.off('canvasReady', closeOnSceneChange);
  },
});

// -------------------------------------
// Region behavior update function
// -------------------------------------
async function updateRegionBehavior(result) {
  // Iterate over all Regions
  for (const [regionId, regionData] of Object.entries(result)) {
    const region = canvas.scene.regions.get(regionId);
    if (!region) continue;

    const updates = [];

    for (const [behaviorId, behaviorInfo] of Object.entries(regionData.behaviors)) {
      const behavior = region.behaviors.get(behaviorId);
      if (!behavior) continue;

      // Compare current state vs. new state
      const currentState = behavior.disabled;
      const newState = behaviorInfo.disabled;
      if (currentState !== newState) {
        updates.push({ _id: behavior.id, disabled: newState });
      }
    }

    // If there are no updates, skip the region
    if (!updates.length) continue;

    // Update the region behaviors in Foundry
    await region.updateEmbeddedDocuments('RegionBehavior', updates);
  }
  ui.notifications.info('Region behavior(s) updated!', { console: false });
}

