    .file(backgroundImage)
    .atLocation({ x: canvas.dimensions.width / 2, y: canvas.dimensions.height / 2 })
    .size({ width: canvas.scene.width / canvas.grid.size, height: canvas.scene.height / canvas.grid.size }, { gridUnits: true })
    .duration(750)
    .fadeOut(750)
    .opacity(0.8)
    .filter('ColorMatrix', { brightness: 4, saturate: -1 })
    .filter('Blur', { blurX: 1, blurY: 1 })
    .zIndex(1)
    .aboveLighting()

    .canvasPan()
    .atLocation({ x: 1680, y: 1260 }) // Set the location
    .duration(1500) // Pan over
    .scale(1.3) // Zoom in
    .lockView(1500) // Lock the view

    //SCREEN SHAKE
    .canvasPan()
    .shake({ duration: 3000, strength: 10, rotation: true, fadeInDuration: 1000, fadeOutDuration: 1500 })
    .wait(500)

    //ENERGY
    .effect()
    .file('blfx.spell.cast.circles1.energy1.blue')
    .atLocation({ x: 1680, y: 1260 })
    .filter('ColorMatrix', { brightness: 1, saturate: -1 })
    .scaleToObject(8)
    .randomRotation()
    .repeats(6, 250, 250)

    //MAP_FLASH_2
    .effect()
    .file(backgroundImage)
    .atLocation({ x: canvas.dimensions.width / 2, y: canvas.dimensions.height / 2 })
    .size({ width: canvas.scene.width / canvas.grid.size, height: canvas.scene.height / canvas.grid.size }, { gridUnits: true })
    .duration(1000)
    .fadeOut(1000)
    .repeats(3, 500, 500)
    .opacity(0.2)
    .filter('ColorMatrix', { brightness: 6, saturate: 2 })
    .filter('Blur', { blurX: 2, blurY: 2 })
    .belowTokens()

    //MAP_FLASH_3
    .effect()
    .file(backgroundImage)
    .atLocation({ x: canvas.dimensions.width / 2, y: canvas.dimensions.height / 2 })
    .size({ width: canvas.scene.width / canvas.grid.size, height: canvas.scene.height / canvas.grid.size }, { gridUnits: true })
    .duration(3000)
    .fadeOut(3000)
    .opacity(0.8)
    .filter('ColorMatrix', { brightness: 2, saturate: -1 })
    .zIndex(1)
    .belowTokens()
    .wait(1250)

    //FIRE_END
    .effect()
    .file('blfx.spell.melee.radial1.lightning1.shock1.blue')
    .atLocation({ x: 1680, y: 1260 })
    .filter('ColorMatrix', { brightness: 1, saturate: -1 })
    .scaleToObject(6)
    //.scale(.5)
    .randomRotation()

    .play();
}
