blfx | Enable/Disable Regions.command:
if (!game.user.isGM) {
  ui.notifications.warn('You do not have permission to use this macro!');
  return;
}

const regions = canvas.scene.regions?.contents || [];
if (regions.length === 0) {
  ui.notifications.info(`'${canvas.scene.name}' has no Regions defined!`, { console: false });
  return;
}

// Sort regions alphabetically by name
regions.sort((a, b) => a.name.localeCompare(b.name));

// Build HTML content
let dialogRegions = '<label><strong>Enable or Disable Region Behaviors</strong></label>';
for (const region of regions) {
  const behaviors = region.behaviors.contents;
  if (behaviors.length === 0) continue;

  dialogRegions += `
    <fieldset>
      <legend>${region.name}</legend>`;

  for (const behavior of behaviors) {
    const checkboxName = `${region.id}-${behavior.id}`;
    dialogRegions += `
        <div style="display: flex; align-items: center;">
          <input type="checkbox" name="${checkboxName}" style="margin-right: 5px; vertical-align: middle;" ${!behavior.disabled ? 'checked' : ''} />
          <label>${behavior.name}</label>
        </div>`;
  }

  dialogRegions += '</fieldset>';
}

let activeDialog = null;
function closeOnSceneChange() {
  if (activeDialog?.rendered) {
    console.log(`Boss Loot | Scene changed, closing the Region Control Dialog.`);
    activeDialog.close(); // calls the instance's close() to destroy the hook
  }
}

// Render the dialog
foundry.applications.api.DialogV2.wait({
  content: dialogRegions,
  rejectClose: false,
  form: { closeOnSubmit: false },
  window: { title: 'Region Control' },

  buttons: [
    {
      action: 'submit',
      label: 'Submit',
      default: true,
      callback: (event, button, dialogElement) => {
        // Grab form data
        const data = new FormDataExtended(button.form).object;
        const result = {};

        for (const region of regions) {
          const behaviors = region.behaviors.contents;
          if (behaviors.length === 0) continue;
          result[region.id] = { name: region.name, behaviors: {} };

          for (const behavior of behaviors) {
            const checkboxName = `${region.id}-${behavior.id}`;
            result[region.id].behaviors[behavior.id] = {
              disabled: !Boolean(data[checkboxName]),
            };
          }
        }
        return result;
      },
    },
    {
      action: 'close',
      label: 'Close',
      callback: () => {},
    },
  ],

  async submit(result) {
    await updateRegionBehavior(result);
  },

  render: (event, dialogElement) => {
    activeDialog = event.target;
    Hooks.on('canvasReady', closeOnSceneChange);
  },

  close: (event, dialogInstance) => {
    // Remove the scene-change hook
    Hooks.off('canvasReady', closeOnSceneChange);
  },
});

// -------------------------------------
// Region behavior update function
// -------------------------------------
async function updateRegionBehavior(result) {
  // Iterate over all Regions
  for (const [regionId, regionData] of Object.entries(result)) {
    const region = canvas.scene.regions.get(regionId);
    if (!region) continue;

    const updates = [];

    for (const [behaviorId, behaviorInfo] of Object.entries(regionData.behaviors)) {
      const behavior = region.behaviors.get(behaviorId);
      if (!behavior) continue;

      // Compare current state vs. new state
      const currentState = behavior.disabled;
      const newState = behaviorInfo.disabled;
      if (currentState !== newState) {
        updates.push({ _id: behavior.id, disabled: newState });
      }
    }

    // If there are no updates, skip the region
    if (!updates.length) continue;

    // Update the region behaviors in Foundry
    await region.updateEmbeddedDocuments('RegionBehavior', updates);
  }
  ui.notifications.info('Region behavior(s) updated!', { console: false });
}

