function closeOnSceneChange() {
  if (activeDialog?.rendered) {
    console.log('Scene changed. Closing the Summon Token dialog.');
    activeDialog.close(); // calls the instance's close() to destroy the hook
  }
}

// --------------------------------------------------------
// DIALOG
// --------------------------------------------------------
foundry.applications.api.DialogV2.wait({
  window: { title: 'Summon Token' },
  form: { closeOnSubmit: true },
  content: dialogContent,
  position: { width: 450 },
  rejectClose: false,

  buttons: [
    {
      action: 'submit',
      label: 'Summon',
      default: true,
      callback: async (event, button, dialogElement) => {
        const data = new FormDataExtended(button.form).object;

        // Summon the tokens
        summonTokens(data);
      },
    },
    {
      action: 'close',
      label: 'Close',
      callback: (event, clickedButton, dialog) => {},
    },
  ],

  render: event => {
    activeDialog = event.target;
    Hooks.on('canvasReady', closeOnSceneChange);

    // Add image update logic
    const selectElement = activeDialog.element.querySelector('#actor-select');
    const imageElement = activeDialog.element.querySelector('#actor-image-preview');

    selectElement?.addEventListener('change', event => {
      const selectedId = event.target.value;
      const selectedActor = actorSpawns.find(actor => actor.id === selectedId);
      if (selectedActor) {
        imageElement.src = selectedActor.img;
      }
    });
  },

  close: (event, dialogInstance) => {
    Hooks.off('canvasReady', closeOnSceneChange);
  },
});

// --------------------------------------------------------
// SUMMON
// --------------------------------------------------------
async function summonTokens(data) {
  const tokenData = [];
  const id = data['actor.id'];
  const hidden = data['actor.hidden'] ?? false;
  const actor = actorSpawns.find(actor => actor.id === id);

  if (!actor) {
    ui.notifications.warn('Please select a token');
    return;
  }

  // const coords = await getSummonCoord(actor.img);
  const tokenPlace = await dnd5e.canvas.TokenPlacement.place({ tokens: [actor.prototypeToken] });
  const { x, y, rotation } = tokenPlace[0];
  const coords = { x, y, rotation };
  tokenData.push(buildTokenData(actor, coords, hidden));

  // Spawn tokens
  if (tokenData.length) {
    const tokenList = await canvas.scene.createEmbeddedDocuments('Token', tokenData);

    // canvas.tokens.releaseAll();
    await summonAnimation(tokenList);
  }
}

// Helper to build Token document data
function buildTokenData(actor, coords, hidden) {
  return {
    name: actor.prototypeToken?.name ?? actor.name,
    x: coords.x,
    y: coords.y,
    rotation: coords.rotation,
    texture: actor.prototypeToken.texture,
    width: actor.prototypeToken.width,
    height: actor.prototypeToken.height,
    sight: actor.prototypeToken.sight,
    hidden: hidden,
    actorId: actor.id,
    actorLink: false,
    detectionModes: actor.prototypeToken.detectionModes,
    displayBars: actor.prototypeToken.displayBars,
    disposition: actor.prototypeToken.disposition,
    displayName: CONST.TOKEN_DISPLAY_MODES.CONTROL,
  };
}

async function summonAnimation(tokenList) {
  if (!game.modules.get('sequencer')?.active) return;
  if (!game.modules.get(MODULE_NAME)?.active) return;

  for (let targetToken of tokenList) {
    new Sequence()
      .wait(200)
      .effect()
      .atLocation(targetToken)
      .file('blfx.spell.template.circle.dust.puff.poison.gas.smoke.radial.1.color7')
      .scaleToObject(2.5)
      .attachTo(targetToken, { bindVisibility: true })

      .play();
  }
}

