async function applyConfig() {
  let updates = 0;

  for (const [sceneId, settings] of Object.entries(config)) {
    const scene = game.scenes.get(sceneId);
    if (!scene) {
      console.warn(`Scene ${sceneId} not found.`);
      continue;
    }

    const diff = foundry.utils.diffObject(scene.environment, settings.environment);
    if (foundry.utils.isEmpty(diff)) {
      console.log(`Scene "${scene.name}" already has correct settings.`);
      continue;
    }

    // Temporarily unlock
    const wasLocked = scene.environment.darknessLock;
    if (wasLocked) await scene.update({ 'environment.darknessLock': false });

    const newEnvironment = foundry.utils.mergeObject(scene.environment, diff, { inplace: false });
    newEnvironment.darknessLock = wasLocked;

    await scene.update({ environment: newEnvironment });
    ui.notifications.info(`Lighting settings updated for scene "${scene.name}".`);
    updates += 1;
  }

  if (updates === 0) ui.notifications.info('No updates were needed for the scenes from "A Trail into the Dark".');
}

blfx | Update Tiles.command:
const MODULE_NAME = 'boss-loot-adventures-premium';
const tiles = canvas.scene.tiles;
const isSequencerActive = game.modules.get('sequencer')?.active;
const damageImunities = ['piercing', 'poison', 'psychic'];

if (!game.user.isGM) {
  ui.notifications.warn('You do not have permission to use this macro!');
  return;
}

if (tiles.size === 0) {
  ui.notifications.info(`'${canvas.scene.name}' have no tiles defined!`, { console: false });
  return;
}

const flaggedTiles = tiles.contents?.filter(tile => {
  // Check for custom flag
  const tileData = foundry.utils.getProperty(tile, `flags.${MODULE_NAME}.tileData`);
  return !!tileData?.name;
});

if (flaggedTiles.length === 0) {
  ui.notifications.info('This script is designed for Boss Loot custom maps and works only on those maps.', { console: false });
  return;
}

// Sort Tiles by name
flaggedTiles.sort((a, b) => {
  const aName = foundry.utils.getProperty(a, `flags.${MODULE_NAME}.tileData.name`) || '';
  const bName = foundry.utils.getProperty(b, `flags.${MODULE_NAME}.tileData.name`) || '';
  return aName.localeCompare(bName);
});

// Build the dialog content
let dialogContent = `<label><strong>Choose how to destroy the webs!</strong></label>`;
dialogContent += `
  <div style="text-align: center;">
    <img src="modules/${MODULE_NAME}/artwork/064-trail-of-webs/assets/Web-Numbers.webp" 
         width="390" height="390" style="border: none;">
  </div>
`;

const damageTypes = CONFIG.DND5E.damageTypes;
const damageTypeOptions = Object.entries(damageTypes)
  .map(([key, val]) => `<option value="${key}">${val.label}</option>`)
  .join('');

dialogContent += `
    <div style="display: flex; flex-direction: row; gap: 10px; margin-bottom: 10px;">
    <div style="flex: 1;">
      <label style="font-size: 0.8em;">Damage Type</label>
      <select name="blfx.damage.type" style="width: 100%;">
        ${Object.entries(damageTypes)
          .map(([key, val]) => `<option value="${key}" ${key === 'fire' ? 'selected' : ''}>${val.label}</option>`)
          .join('')}
      </select>
    </div>
    </div>`;

dialogContent += `<fieldset><legend>Webs on ${canvas.scene.name}</legend>`;
for (const tile of flaggedTiles) {
  const tileData = foundry.utils.getProperty(tile, `flags.${MODULE_NAME}.tileData`);
  if (!tileData?.name) continue;

  dialogContent += `
    <div style="display: flex; align-items: center; gap: 10px;">
      <img src="${tileData.img}" width="48" height="48" style="border: none;">
      <label style="flex: 1;">${tileData.name}</label>
      <input type="checkbox" name="tile-${tile.id}" />
    </div>
  `;
}

dialogContent += '</fieldset>';

let activeDialog = null;
function closeOnSceneChange() {
  if (activeDialog?.rendered) {
    console.log(`Boss Loot | Scene changed, closing the Tiles Control Dialog.`);
    activeDialog.close(); // calls the instance's close() to destroy the hook
  }
}

// Render the dialog
foundry.applications.api.DialogV2.wait({
  content: dialogContent,
  rejectClose: false,
  form: { closeOnSubmit: false },
  window: { title: 'Tiles Control' },

  buttons: [
    {
      action: 'update',
      label: 'Update Webs',
      default: true,
      callback: async (event, button, dialogElement) => {
        const data = new FormDataExtended(button.form).object;
        if (isSequencerActive) {
          await updateTilesWithSequencer(data);
        } else {
          await updateTiles(data);
        }

        // Clear the checkboxes
        button.form?.querySelectorAll('input[type="checkbox"]').forEach(checkbox => (checkbox.checked = false));
      },
    },
    {
      action: 'restore',
      label: 'Restore Webs',
      default: false,
      callback: async () => {
        await restoreTiles();
      },
    },
  ],

  render: (event, dialogElement) => {
    activeDialog = event.target;
    Hooks.on('canvasReady', closeOnSceneChange);
  },

  close: (event, dialogInstance) => {
    // Remove the scene-change hook
    Hooks.off('canvasReady', closeOnSceneChange);
  },
});

async function updateTiles(data) {
  const updates = [];
  const damageType = data['blfx.damage.type'].trim();
  if (damageImunities.includes(damageType)) {
    ui.notifications.info(`The web is immune to ${damageType} damage!`, { console: false });
    return;
  }

  const selectedTilesIds = Object.entries(data)
    .filter(([key, checked]) => key.startsWith('tile-') && checked === true)
    .map(([id, _]) => id.replace(/^tile-/, ''));

  for (const id of selectedTilesIds) {
    const tile = tiles.get(id);
    if (!tile.texture.src) continue; // skip tiles that don't have a texture
    const flagData = foundry.utils.getProperty(tile, `flags.${MODULE_NAME}.tileData`);
    const newTexture = damageType === 'fire' ? flagData.burning : flagData.shredded;
    updates.push({ _id: tile.id, 'texture.src': null, 'video.loop': false, 'video.autoplay': true });
    await tile.update({ 'texture.src': newTexture, 'video.loop': false, 'video.autoplay': true });
  }

  await wait(500);
  // Update the
  if (updates.length > 0) {
    await canvas.scene.updateEmbeddedDocuments('Tile', updates);
  }
}

async function restoreTiles() {
  const updates = [];

  for (const tile of flaggedTiles) {
    const defaultTexture = foundry.utils.getProperty(tile, `flags.${MODULE_NAME}.tileData`);
    updates.push({ _id: tile.id, 'texture.src': defaultTexture.img, 'video.loop': false, 'video.autoplay': true });
  }

  // Updates
  if (updates.length > 0) {
    await canvas.scene.updateEmbeddedDocuments('Tile', updates);
    ui.notifications.info(`All webs restored!`, { console: false });
  }
}

async function updateTilesWithSequencer(data) {
  const damageType = data['blfx.damage.type'].trim();
  if (damageImunities.includes(damageType)) {
    ui.notifications.info(`The web is immune to ${damageType} damage!`, { console: false });
    return;
  }

  const selectedTilesIds = Object.entries(data)
    .filter(([key, checked]) => key.startsWith('tile-') && checked === true)
    .map(([id, _]) => id.replace(/^tile-/, ''));

  for (const id of selectedTilesIds) {
    const tile = tiles.get(id);
    if (!tile.texture.src) continue; // skip tiles that don't have a texture
    const flagData = foundry.utils.getProperty(tile, `flags.${MODULE_NAME}.tileData`);
    const newTexture = damageType === 'fire' ? flagData.burning : flagData.shredded;
    await playSequencerAnimation(tile, newTexture);
    await wait(200);
    await tile.update({ 'texture.src': null, 'video.loop': false, 'video.autoplay': true });
  }
}

async function playSequencerAnimation(tileDoc, animationFile) {
  await new Sequence().effect().file(animationFile).atLocation(tileDoc).scaleToObject(1).startTime(500).play();
}

async function wait(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

